mapper.select:
SELECT BOARD_ID, TITLE, CONTENT, VIEWS, TAGS, CREATED_AT, UPDATED_AT, DELETE_YN
FROM   BOARD
WHERE  BOARD_ID = #{boardId}

mapper.update:
UPDATE BOARD
SET    VIEWS = VIEWS + 1
WHERE  BOARD_ID = #{boardId}

UPDATE BOARD
SET    DELETE_YN = 'Y'
WHERE  BOARD_ID = #{boardId}

UPDATE BOARD
SET    TITLE=#{title}, CONTENT=#{content}, UPDATED_AT=NOW()
WHERE  BOARD_ID=#{board_id}
